queries_list.readingtype:
SELECT DISTINCT "ReadingTypeId" from "VersionReadingTypes" where "VersionCode" in (SELECT "Version" from "Sensor" where "SensorId" in 
	(WITH RECURSIVE "SensorList" AS 
		(SELECT "Sensor".* FROM "Sensor" where "Sensor"."SensorId" in 
		(SELECT "Sensor"."SensorId" FROM "Sensor" JOIN "MemberSensor" ON "MemberSensor"."SensorId" = "Sensor"."SensorId" AND "MemberId" in
		(SELECT "MemberId" from "Member" where "UserName" in ('intas')) 
		)
		UNION ALL SELECT d.* FROM "Sensor" AS d JOIN "SensorList" AS sd ON (d."ParentSensorId" = sd."SensorId") 
		)
		SELECT DISTINCT "Sensor"."SensorId" FROM "Sensor" Join "SensorList" on "Sensor"."SensorId" = "SensorList"."SensorId" 
	)
)
ORDER BY "ReadingTypeId" ASC
